import React, { FunctionComponent, useState } from 'react';
import Pokemon from '../models/pokemon';
import './pokemon-card.css';

type Props = {
  pokemon: Pokemon,
  borderColorCardPokemon? : string
};
  

const PokemonCard: FunctionComponent<Props> = ({pokemon, borderColorCardPokemon = '#009688'}) => {

  const [color, setColor] = useState<string>();
    
  const showBorder = () => {
      setColor(borderColorCardPokemon);//On ajoute une nouvelle couleur à la bordure pour ajouter un effet navigation
  }

  const hideColorBorder = () => {
      setColor('#f5f5f5');//On remet la bordure en gris
  }

  const formatDate = (date: Date): string => {
    return `${date.getDate()}/${date.getMonth()}/${date.getFullYear()}`
  }

  const formatType = (type: string): string => {
    let color: string;
   
    switch (type) {
      case 'Feu': 
        color = 'red lighten-1'; 
        break; 
      case 'Eau': 
        color = 'blue lighten-1'; 
        break; 
      case 'Plante': 
        color = 'green lighten-1'; 
        break; 
      case 'Insecte': 
        color = 'brown lighten-1'; 
        break; 
      case 'Normal': 
        color = 'grey lighten-3'; 
        break; 
      case 'Vol': 
        color = 'blue lighten-3'; 
        break; 
      case 'Poison': 
        color = 'deep-purple accent-1'; 
        break; 
      case 'Fée': 
        color = 'pink lighten-4'; 
        break; 
      case 'Psy': 
        color = 'deep-purple darken-2'; 
        break; 
      case 'Electrik': 
        color = 'lime accent-1'; 
        break; 
      case 'Combat': 
        color = 'deep-orange'; 
        break; 
      default: 
        color = 'grey'; 
        break; 
    }
   
    return `chip ${color}`;
  }

  return (
    <div className="col s6 m4" onMouseEnter={showBorder} onMouseLeave={hideColorBorder}>
      <div className="card horizontal" style={{ borderColor: color}}>
        <div className="card-image"> 
          <img src={pokemon.picture} alt={pokemon.name}/>
        </div>
        <div className="card-stacked">
          <div className="card-content">
            <p>{pokemon.name}</p>
            <p><small>{formatDate(pokemon.created)}</small></p>
            {pokemon.types.map(type => (<span style={{ borderColor: formatType(type)}} key={type}>{type} </span>))}
          </div>
        </div>
      </div> 
    </div>
  );
}
export default PokemonCard;
